// Using fetch API (Node.js 18+ or browser)
const url = "https://astro-api.procoders.pro/api/v3/data/now";

try {
    const response = await fetch(url, {
        method: 'GET',
        headers: {
            'Accept': 'application/json'
        }
    });

    const result = await response.json();

    if (response.ok) {
        console.log('‚úÖ Success:', result);
    } else {
        console.error('‚ùå Error:', response.status, result);
    }
} catch (error) {
    console.error('‚ùå Network error:', error);
}

// Using axios library
const axios = require('axios');

const url = "https://astro-api.procoders.pro/api/v3/data/now";

axios.get(url)
    .then(response => {
        console.log('‚úÖ Success:', response.data);
    })
    .catch(error => {
        if (error.response) {
            console.error('‚ùå Error:', error.response.status, error.response.data);
        } else {
            console.error('‚ùå Network error:', error.message);
        }
    });

package main

import (
    "encoding/json"
    "fmt"
    "io"
    "net/http"
)

func main() {
    url := "https://astro-api.procoders.pro/api/v3/data/now"

    resp, err := http.Get(url)
    if err != nil {
        fmt.Printf("‚ùå Error making request: %v\n", err)
        return
    }
    defer resp.Body.Close()

    body, err := io.ReadAll(resp.Body)
    if err != nil {
        fmt.Printf("‚ùå Error reading response: %v\n", err)
        return
    }

    if resp.StatusCode == 200 {
        fmt.Println("‚úÖ Success!")
        var result map[string]interface{}
        json.Unmarshal(body, &result)
        prettyJSON, _ := json.MarshalIndent(result, "", "  ")
        fmt.Println(string(prettyJSON))
    } else {
        fmt.Printf("‚ùå Error: %d\n", resp.StatusCode)
        fmt.Println(string(body))
    }
}

// Using fetch API (Node.js 18+ or browser)
const url = "https://astro-api.procoders.pro/api/v3/data/positions";

const data = {
  "subject": {
    "name": "John Doe",
    "birth_data": {
      "year": 1990,
      "month": 5,
      "day": 15,
      "hour": 14,
      "minute": 30,
      "city": "London",
      "country_code": "GB"
    }
  },
  "options": {
    "house_system": "P",
    "zodiac_type": "Tropic",
    "active_points": [
      "Sun",
      "Moon",
      "Mercury",
      "Venus",
      "Mars",
      "Jupiter",
      "Saturn"
