        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
    });

    const result = await response.json();

    if (response.ok) {
        console.log('‚úÖ Success:', result);
    } else {
        console.error('‚ùå Error:', response.status, result);
    }
} catch (error) {
    console.error('‚ùå Network error:', error);
}

