  "subject": {
    "name": "John Doe",
    "birth_data": {
      "year": 1990,
      "month": 5,
      "day": 15,
      "hour": 14,
      "minute": 30,
      "city": "London",
      "country_code": "GB"
    }
  },
  "transit_date": {
    "year": 2024,
    "month": 12,
    "day": 25,
    "hour": 12,
    "minute": 0
  },
  "options": {
