import requests
import json

# API endpoint
url = "https://astro-api.procoders.pro/api/v3/analysis/synastry-report"

# Request data
data = {
  "person1": {
    "name": "Alice",
    "birth_data": {
      "year": 1990,
      "month": 5,
      "day": 15,
      "hour": 14,
      "minute": 30,
      "city": "Paris",
      "country_code": "FR"
    }
  },
  "person2": {
    "name": "Bob",
    "birth_data": {
      "year": 1988,
      "month": 8,
      "day": 22,
      "hour": 18,
      "minute": 45,
      "city": "Lisbon",
      "country_code": "PT"
    }
  },
  "options": {
    "house_system": "P",
    "orb_set": "tight"
  }
}

# Make the request
response = requests.post(
    url,
    headers={"Content-Type": "application/json"},
    json=data
)

# Handle response
if response.status_code == 200:
    result = response.json()
    print("‚úÖ Success!")
    print(json.dumps(result, indent=2))
else:
    print(f"‚ùå Error: {response.status_code}")
    print(response.text)

