const url = "https://astro-api.procoders.pro/api/v3/analysis/direction-report";
const data = {
  "subject": {
    "name": "John Doe",
    "birth_data": {
      "year": 1990,
      "month": 5,
      "day": 15,
      "hour": 14,
      "minute": 30,
      "city": "London",
      "country_code": "GB"
    }
  },
  "options": {
    "house_system": "P",
    "zodiac_type": "Tropic",
    "active_points": [
      "Sun",
      "Moon",
      "Mercury",
      "Venus",
      "Mars",
      "Jupiter",
      "Saturn"
    ],
    "precision": 2
  }
};

axios.post(url, data)
    .then(response => {
        console.log('‚úÖ Success:', response.data);
    })
    .catch(error => {
        if (error.response) {
            console.error('‚ùå Error:', error.response.status, error.response.data);
        } else {
            console.error('‚ùå Network error:', error.message);
        }
    });

package main

import (
    "bytes"
    "encoding/json"
    "fmt"
    "io"
    "net/http"
)

func main() {
    url := "https://astro-api.procoders.pro/api/v3/analysis/direction-report"

    data := map[string]interface{}{
  "subject": {
    "name": "John Doe",
    "birth_data": {
      "year": 1990,
      "month": 5,
      "day": 15,
      "hour": 14,
      "minute": 30,
      "city": "London",
      "country_code": "GB"
    }
  },
  "options": {
    "house_system": "P",
    "zodiac_type": "Tropic",
    "active_points": [
      "Sun",
      "Moon",
      "Mercury",
      "Venus",
      "Mars",
      "Jupiter",
      "Saturn"
    ],
    "precision": 2
  }
}

    jsonData, err := json.Marshal(data)
    if err != nil {
        fmt.Printf("‚ùå Error marshaling JSON: %v\n", err)
        return
    }

    resp, err := http.Post(url, "application/json", bytes.NewBuffer(jsonData))
    if err != nil {
        fmt.Printf("‚ùå Error making request: %v\n", err)
        return
    }
    defer resp.Body.Close()

    body, err := io.ReadAll(resp.Body)
    if err != nil {
        fmt.Printf("‚ùå Error reading response: %v\n", err)
        return
    }

    if resp.StatusCode == 200 {
        fmt.Println("‚úÖ Success!")
        var result map[string]interface{}
        json.Unmarshal(body, &result)
        prettyJSON, _ := json.MarshalIndent(result, "", "  ")
        fmt.Println(string(prettyJSON))
    } else {
        fmt.Printf("‚ùå Error: %d\n", resp.StatusCode)
        fmt.Println(string(body))
    }
}

